<?phpclass Configs extends Controller{    function __construct()    {        parent:: __construct();                        $this->path_to_system = ADMIN;                $this->load_model('configs_model');                $this->view->view = 'configs';    }        function index() {}    public function change_pass()    {                if (isset($_POST['go_pass'])) {            $result = $this->configs_model->change_password($_POST['old_pass'], $_POST['new_pass'], $_POST['rtr_pass']);            switch ($result) {                case 1:                    $this->view->message = '<font color="red">Вы ввели не все данные.</font>';                    $this->view->message_type = 'error';                    break;                case 2:                    $this->view->message = '<font color="red">Пароли не совпадают.</font>';                    $this->view->message_type = 'error';                    break;                case 3:                    $this->view->message = '<font color="red">Старый пароль не подходит.</font>';                    $this->view->message_type = 'error';                    break;                default:                    $this->view->message = 'Вы успешно сменили пароль.';                    $this->view->message_type = 'success';                    break;            }        }    }}?>